import React, { useState } from 'react';
import axios from 'axios';
import LoginFormView from './LoginFormView';

/**
 * LoginForm.jsx
 *
 * Componente contenedor que gestiona el estado del formulario de login
 * y realiza la autenticación contra el backend.
 *
 * Props:
 * - setToken: function — actualiza el token en el estado global tras login exitoso
 * - setShowRegister: function — alterna entre vista de login y registro
 * - showRegister: boolean — indica si actualmente se muestra la vista de registro
 */
const LoginForm = ({ setToken, setShowRegister, showRegister }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  /**
   * Maneja el envío del formulario de login.
   * Envía credenciales al backend y guarda el token si la autenticación es exitosa.
   *
   * @param {Event} e - Evento de envío del formulario
   */
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      console.log("Enviando login:", username, password);

      const { data } = await axios.post(
        'http://localhost:8080/auth/login',
        { username, password }
      );

      setToken(data); // Actualiza el estado global con el token
      localStorage.setItem("token", data); // Persiste el token en localStorage
      alert("Login successful");
    } catch (error) {
      console.error("Error en login:", error);
      alert("Invalid username or password");
    }
  };

  // Renderiza la vista del formulario, pasando props controladas
  return (
    <LoginFormView
      username={username}
      password={password}
      onUserNameChange={(e) => setUsername(e.target.value)}
      onPasswordChange={(e) => setPassword(e.target.value)}
      onSubmit={handleSubmit}
      setShowRegister={setShowRegister}
      showRegister={showRegister}
    />
  );
};

export default LoginForm;
