import React, {useState} from "react";
import axios from "axios";

const IngresoForm = () => {
    const [concepto,setConcepto] =  useState('');
    const [monto, setMonto] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const newIngreso = {concepto,monto};
        axios.post("http://localhost:8080/api/ingreso",newIngreso).then(response => {
            console.log('Recorder income', response.data);
            setConcepto('');
            setMonto('');
        }).catch(error => ('Error registering entry', error));
    };
    return(
        <form onSubmit={handleSubmit}>
            <input type ="double" placeholder="ingreso" value={monto} onChange={(e) => setMonto(e.target.value)}/>
            <input type ="text" placeholder="concepto" value={concepto} onChange={(e) => setConcepto(e.target.value)}/>

        </form>
    );
};
export default IngresoForm;
