// Importa estilos globales de la aplicaciÃ³n
import './App.css';

// Importa React y el hook useState para manejar el estado local
import React, { useState } from "react";

// Importa componentes de React Router para manejar las rutas del frontend
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';

// Importa los componentes que representan cada pantalla o funcionalidad
import CapitalGet from './components/CapitalComponent/CapitalGet';
import GastoList from './components/GastoComponent/GastoList';
import GastoForm from './components/GastoComponent/GastoForm';
import IngresoForm from './components/IngresoComponent/IngresoForm';
import LoginForm from './components/LoginComponent/LoginForm';
// ğŸ“Œ LoginForm no se usa en las rutas actuales porque se renderiza solo antes del login
//   Puede integrarse mÃ¡s adelante en React Router si quieres un endpoint /login

// Componente principal de la aplicaciÃ³n
const App = () => {

  // Estado global bÃ¡sico para manejar el token de autenticaciÃ³n
  const [token, setToken] = useState(null);

  // ğŸ”¹ Si no hay token, el usuario no estÃ¡ autenticado â†’ mostrar pantalla de login
  if (!token) {
    return (
      <div className="bgConainerLogin">
        {/* LoginForm recibe setToken como prop para guardar el token tras autenticarse */}
        <LoginForm setToken={setToken} />
      </div>
    );
  }

  // ğŸ”¹ Si el usuario estÃ¡ autenticado â†’ se muestra el router con el menÃº y las rutas
  return (
    <Router> {/* Componente envolvente que habilita el manejo de rutas en la SPA */}
      <div>
        {/* Barra de navegaciÃ³n principal */}
        <nav className="bar">
          <ul className='flex justify-around'>
            <li><Link to="/gastoForm">Registrar Gasto</Link></li>
            <li><Link to="/ingresoForm">Registrar Ingreso</Link></li>
            <li><Link to="/gastoList">Lista de Gastos</Link></li>
            <li><Link to="/capital">Capital</Link></li>
            {/*
              ğŸ”¹ Si quieres aÃ±adir un link directo a login para pruebas o logout, 
              podrÃ­as colocarlo aquÃ­.
            */}
          </ul>
        </nav>

        {/* Contenedor principal del contenido segÃºn la ruta */}
        <div className="container mx-auto p-8">
          <Routes>
            {/* Formulario para registrar un nuevo gasto */}
            <Route path="/gastoForm" element={<GastoForm />} />
            {/* Formulario para registrar un nuevo ingreso */}
            <Route path="/ingresoForm" element={<IngresoForm />} />
            {/* Listado de todos los gastos registrados */}
            <Route path="/gastoList" element={<GastoList />} />
            {/* VisualizaciÃ³n del capital actual */}
            <Route path="/capital" element={<CapitalGet />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

// Exporta el componente para que pueda ser usado en index.js
export default App;
