import { useState } from "react";
import axios from "axios";
import RegisterCompView from "./RegisterCompView";

/**
 * Register.jsx
 *
 * Componente contenedor que gestiona el estado del formulario de registro
 * y realiza la petición POST al backend para crear un nuevo usuario.
 *
 * Estado:
 * - username: string — nombre de usuario ingresado
 * - password: string — contraseña ingresada
 * - message: string | null — mensaje de éxito o error tras el intento de registro
 */
const Register = () => {
    const [username, setUserName] = useState("");
    const [password, setPassword] = useState("");
    const [message, setMessage] = useState(null);

    /**
     * Maneja el envío del formulario de registro.
     * Envía los datos al backend y actualiza el estado con el resultado.
     *
     * @param {Event} e - Evento de envío del formulario
     */
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post("http://localhost:8080/auth/register", {
                username: userName,
                password,
            });

            setMessage("Registro exitoso");

            // Redirige al login después de 2 segundos
            setTimeout(() => setShowRegister(false), 2000);

            setUserName("");
            setPassword("");
        } catch (err) {
            setMessage(err.response?.data || "Error al registrar");
        }
    };


    // Renderiza la vista del formulario, pasando props controladas
    return (
        <RegisterCompView
            userName={userName}
            password={password}
            onUserNameChange={(e) => setUserName(e.target.value)}
            onPasswordChange={(e) => setPassword(e.target.value)}
            onSubmit={handleSubmit}
            setShowRegister={setShowRegister}
            message={message} // Puedes mostrar este mensaje en la vista si lo deseas
        />
    );
};

export default Register;
