import React, { useState } from "react";
import axios from "axios";
import IngresoFormView from "./IngresoFormView";

const IngresoForm = () => {
  const [concepto, setConcepto] = useState('');
  const [monto, setMonto] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const newIngreso = { concepto, monto };
    axios.post("http://localhost:8080/api/ingreso", newIngreso)
      .then(response => {
        console.log('Ingreso registrado', response.data);
        setConcepto('');
        setMonto('');
      })
      .catch(error => console.error('Error al registrar ingreso', error));
  };

  return (
    <form onSubmit={handleSubmit} className="max-w-md mx-auto mt-10 p-6 bg-white rounded-2xl shadow-lg space-y-4">
      <h2 className="text-2xl font-bold text-center">Registrar Ingreso</h2>
      <input
        type="number"
        placeholder="Monto"
        value={monto}
        onChange={(e) => setMonto(e.target.value)}
        className="w-full p-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400"
      />
      <input
        type="text"
        placeholder="Concepto"
        value={concepto}
        onChange={(e) => setConcepto(e.target.value)}
        className="w-full p-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-400"
      />
      <button
        type="submit"
        className="w-full bg-blue-500 hover:bg-blue-600 text-white font-semibold py-3 rounded-xl transition duration-200"
      >
        Registrar
      </button>
    </form>
  );
};

export default IngresoForm;
