import React, {useState} from "react";
import axios from "axios";

const GastoForm = () => {
    const[concepto, setConcepto] = useState('');
    const[monto, setMonto] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const newGasto = {concepto, monto};
        axios.post("http://localhost:8080/api/gasto", newGasto).then (response => {
            console.log("Spent registered:",response.data);
            setConcepto('');
            setMonto('');

        }).catch(error => ("Error adding spent: ", error));
    };
    return (
        <form onSubmit={handleSubmit}>
            <input type="text" placeholder="Concepto: " value={concepto} onChange={(e) => setConcepto(e.target.value)}/>
            <input type="double" placeholder="Monto: " value={monto} onChange = {(e) => setConcepto(e.target.value)}/>
            <button type="submmit">Registrar</button>
        </form>
    );
};
export default GastoForm;
