import React from "react";

const GastoListView = ({ gastos }) => {
  return (
    <div className="max-w-4xl mx-auto mt-10">
      <h2 className="text-2xl font-bold mb-4 text-center">Lista de Gastos</h2>
      <div className="overflow-x-auto">
        <table className="min-w-full border border-gray-300 rounded-xl shadow">
          <thead className="bg-blue-500 text-white">
            <tr>
              <th className="px-4 py-2 text-left">Concepto</th>
              <th className="px-4 py-2 text-left">Monto</th>
              <th className="px-4 py-2 text-left">Fecha</th>
            </tr>
          </thead>
          <tbody>
            {gastos.map((gasto, index) => (
              <tr key={index} className="border-t">
                <td className="px-4 py-2">{gasto.concepto}</td>
                <td className="px-4 py-2">${gasto.monto}</td>
                <td className="px-4 py-2">
                {(() => {
                    const [year, month, day] = gasto.fecha.slice(0, 10).split("-");
                    return `${day}-${month}-${year}`;
                })()}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default GastoListView;
