import React, {useState} from "react";
import axios from "axios";
import GastoFormView from "./GastoFormView";

const GastoForm = () => {
    const[concepto, setConcepto] = useState('');
    const[monto, setMonto] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const newGasto = {concepto, monto};
        axios.post("http://localhost:8080/api/gasto", newGasto).then (response => {
            console.log("Spent registered:",response.data);
            setConcepto('');
            setMonto('');

        }).catch(error => console.error('Error adding spent:',error));
    };
    return (
        <GastoFormView 
        monto={monto}
        concepto={concepto}
        onMontoChange={(e) => setMonto(e.target.value)}
        onConceptoChange={(e) => setConcepto(e.target.value)}
        onSubmit={handleSubmit}
        />    
    );
};
export default GastoForm;
